subprojects {
    apply(plugin = "java")
    apply(plugin = "io.micronaut.test-resources")
    apply(plugin = "io.micronaut.aot")

    repositories { mavenCentral() }

    extensions.getByType(JavaPluginExtension::class).apply {
        toolchain {
            languageVersion.set(JavaLanguageVersion.of(21))
        }
    }
}