buildscript {
    repositories {
        maven {
            url = uri("https://plugins.gradle.org/m2/")
        }
    }
    dependencies {
        classpath("com.github.johnrengelman:shadow:8.1.1")
        classpath("io.micronaut.gradle:micronaut-test-resources-plugin:4.4.2")
        classpath("io.micronaut.gradle:micronaut-aot-plugin:4.4.2")
        classpath("io.micronaut.gradle:micronaut-gradle-plugin:4.4.2")
    }
}



plugins {
    id("com.gradle.develocity") version "3.18.1"
}

develocity {
    buildScan {
        termsOfUseUrl.set("https://gradle.com/help/legal-terms-of-use")
        termsOfUseAgree.set("yes")
    }
}

rootProject.name = "micronaut-todoapp"

include("api", "model", "notification")